const wechatHeaders = {
  extspam:
    "",
  "client-version": "2.0.0",
};

const wechatUrls = ["https://wx.qq.com/*", "https://web.wechat.com/*", "https://wx2.qq.com/*", "https://wx8.qq.com/*"];

chrome.webRequest.onBeforeRequest.addListener(
  function (details) {
    const url = new URL(details.url);
    if (url.searchParams.get("target") !== 't') {
		url.searchParams.set("target", 't');
		return { redirectUrl: url.href };
    }
    return {};
  },
  { urls: wechatUrls },
  ["blocking"]
);

chrome.webRequest.onBeforeSendHeaders.addListener(
  function (details) {
    const url = new URL(details.url);
    if (url.pathname == "/cgi-bin/mmwebwx-bin/webwxnewloginpage") {
      for (var k in wechatHeaders) {
        details.requestHeaders.push({
          name: k,
          value: wechatHeaders[k],
        });
      }
      return { requestHeaders: details.requestHeaders };
    }
    return {};
  },
  { urls: wechatUrls },
  ["blocking", "requestHeaders"]
);
